require('svelte/register');
const fs = require('fs');
const path = require('path');
const CleanCSS = require('clean-css');
const { minify } = require("html-minifier");
const config = require('./config.json');

const includeJS = config.hydrate;

const App = require('./build/App.js');

const templatePath = './src/template.svelte';
const outDir = 'public';

const Template = require(templatePath).default;

const { head, html, css } = App.render({});
const data = Template.render({ includeJS, head, html });

const cssOptions = {};
const minifiedCss = new CleanCSS(cssOptions).minify(css.code).styles;

const htmlOptions = { collapseWhitespace: true };
const minifiedHtml = minify(data.html, htmlOptions);

fs.writeFileSync(path.join(outDir, 'index.html'), minifiedHtml, 'utf-8');
fs.writeFileSync(path.join(outDir, 'styles.css'), minifiedCss, 'utf-8');
fs.writeFileSync(path.join(outDir, 'styles.css.map'), css.map, 'utf-8');

{
	"hydrate": false
}

import svelte from 'rollup-plugin-svelte';
import resolve from '@rollup/plugin-node-resolve';
import commonjs from '@rollup/plugin-commonjs';
import dsv from '@rollup/plugin-dsv';
import livereload from 'rollup-plugin-livereload';
import { terser } from 'rollup-plugin-terser';
import execute from "rollup-plugin-execute";
import json from "@rollup/plugin-json";
import config from './config.json';

const production = !process.env.ROLLUP_WATCH;

export default {
	input: 'src/main.js',
	output: {
		sourcemap: true,
		format: 'iife',
		name: 'app',
		file: 'public/build/bundle.js'
	},
	plugins: [
		// Allow for importing csv files as modules
		dsv(),
		// And importing json files
		json(),

		svelte({
			// enable run-time checks when not in production
			dev: !production,
			// we'll extract any component CSS out into
			// a separate file — better for performance
			css: css => {
				css.write('public/build/bundle.css');
			},
			hydratable: config.hydrate
		}),

		// If you have external dependencies installed from
		// npm, you'll most likely need these plugins. In
		// some cases you'll need additional configuration —
		// consult the documentation for details:
		// https://github.com/rollup/plugins/tree/master/packages/commonjs
		resolve({
			browser: true,
			dedupe: importee => importee === 'svelte' || importee.startsWith('svelte/')
		}),
		commonjs(),

		// Copy the template over
		execute(`node copy-template.js ${production}`),

		// In dev mode, call `npm run start` once
		// the bundle has been generated
		!production && serve(),

		// Watch the `public` directory and refresh the
		// browser on changes when not in production
		!production && livereload('public'),

		// If we're building for production (npm run build
		// instead of npm run dev), minify
		production && terser()

	],
	watch: {
		clearScreen: false
	}
};

function serve() {
	let started = false;

	return {
		writeBundle() {
			if (!started) {
				started = true;

				require('child_process').spawn('npm', ['run', 'start', '--', '--dev'], {
					stdio: ['ignore', 'inherit', 'inherit'],
					shell: true
				});
			}
		}
	};
}

html, body {
	position: relative;
	width: 100%;
	height: 100%;
}

body {
	color: #333;
	margin: 0;
	padding: 8px;
	box-sizing: border-box;
	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif;
}

a {
	color: rgb(0,100,200);
	text-decoration: none;
}

a:hover {
	text-decoration: underline;
}

a:visited {
	color: rgb(0,80,160);
}

label {
	display: block;
}

input, button, select, textarea {
	font-family: inherit;
	font-size: inherit;
	padding: 0.4em;
	margin: 0 0 0.5em 0;
	box-sizing: border-box;
	border: 1px solid #ccc;
	border-radius: 2px;
}

input:disabled {
	color: #ccc;
}

button {
	color: #333;
	background-color: #f4f4f4;
	outline: none;
}

button:disabled {
	color: #999;
}

button:not(:disabled):active {
	background-color: #ddd;
}

button:focus {
	border-color: #666;
}

